import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const apiUrl = "http://localhost:8080/auth"; // Adjust this if necessary

function Login({ setToken }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const response = await fetch(`${apiUrl}/${username}/${password}`);
    const data = await response.json()?response.json():null;

    if (data && data.idUser) {
      setToken(data);
      navigate("/tasks"); // Redirect to task manager
    } else {
      alert("Invalid login credentials. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <h1>Login</h1>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Log In</button>
      </form>
      <p>
        Don't have an account?{" "}
        <a href="/signup">Create one</a>
      </p>
    </div>
  );
}

export default Login;
