import React, { useEffect, useState } from "react";
import CreateTask from "./CreateTask";
import TaskList from "./TaskList";

const apiUrl = "http://localhost:8080/tasks";

function TaskManager({ token, setToken }) {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    if (token) {
      fetchTasks();
    }
  }, [token]);

  const fetchTasks = async () => {
    const response = await fetch(`${apiUrl}/GAT/${token.idUser}`);
    const data = await response.json();
    setTasks(data);
  };

  const handleLogout = async () => {
    await fetch(`${apiUrl}/auth`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ idToken: token._id }),
    });
    setToken(null); // Clear the token and redirect to login
  };

  return (
    <div>
      <section className="task-manager">
        <h2>Administrador de Tareas</h2>
        <button onClick={handleLogout}>Log Out</button>
        <CreateTask createTask={createTask} />
        <TaskList tasks={tasks} updateTask={updateTask} deleteTask={deleteTask} />
      </section>
    </div>
  );
}


export default TaskManager;
